import React from 'react';
import './HomePage.css'; // Optional: Import CSS for styling
import HeroBanner from './components/HeroBanner'; // Import your hero banner component
import ProductList from './components/ProductList'; // Import a component for featured products
import CategoryList from './components/CategoryList'; // Import a component for categories

const HomePage = () => {
  return (
    <div className="home-page">
      {/* Hero Banner Section */}
      <HeroBanner />

      {/* Featured Products Section */}
      <section className="featured-products">
        <h2>Featured Products</h2>
        <ProductList />
      </section>

      {/* Categories Section */}
      <section className="categories">
        <h2>Shop by Category</h2>
        <CategoryList />
      </section>

      {/* Promotional Offers Section */}
      <section className="promotions">
        <h2>Special Offers</h2>
        <div className="promotion-banner">
          <p>Get 20% off on your first order!</p>
          <button className="btn-promo">Shop Now</button>
        </div>
      </section>
    </div>
  );
};

export default HomePage;
