import { useState } from 'react';
import { Navbar, Nav, Offcanvas, Button } from 'react-bootstrap';

export default function NavBar({ logo }) {
    const [show, setShow] = useState(false);

    const handleToggle = () => setShow(!show);
    const handleClose = () => setShow(false);

    return (
        <>
            <Navbar bg="light" expand="lg" className="menu-bar">
                <Navbar.Toggle onClick={handleToggle} aria-controls="offcanvasNavbar" />
                <Navbar.Brand href="#">
                    <img
                        src={logo}
                        alt="Brand Logo"
                        className="brand-logo d-block d-lg-none mx-auto"
                    />
                </Navbar.Brand>
                {/* Offcanvas for the menu */}
                <Offcanvas show={show} onHide={handleClose} placement="end">
                    <Offcanvas.Header closeButton>
                        <Offcanvas.Title>Menu</Offcanvas.Title>
                    </Offcanvas.Header>
                    <Offcanvas.Body>
                        <Nav className="flex-column">
                            <Nav.Link href="/" onClick={handleClose}>Home</Nav.Link>
                            <Nav.Link href="/products" onClick={handleClose}>Products</Nav.Link>
                            <Nav.Link href="/about" onClick={handleClose}>About Us</Nav.Link>
                            <Nav.Link href="/contact" onClick={handleClose}>Contact</Nav.Link>
                        </Nav>
                    </Offcanvas.Body>
                </Offcanvas>
            </Navbar>
        </>
    );
}
