import { useState } from 'react';
import { Navbar, Nav } from 'react-bootstrap';
import './NavBar.css';

export default function NavBar({ logo }) {
    const [isMobileMenuOpen, setMobileMenuOpen] = useState(false);

    const handleToggle = () => setMobileMenuOpen(prev => !prev);
    const handleClose = () => setMobileMenuOpen(false);

    return (
        <Navbar bg="light" expand="lg" className="menu-bar">
            <Navbar.Toggle onClick={handleToggle} aria-controls="menubar" />
            <Navbar.Brand href="#">
                <img
                    src={logo}
                    alt="Brand Logo"
                    className="brand-logo d-block d-lg-none mx-auto"
                />
            </Navbar.Brand>
            <Navbar.Collapse id="menubar" in={isMobileMenuOpen}>
                <Nav className="me-auto">
                    <Nav.Link href="/" className="nav-item" onClick={handleClose}>Home</Nav.Link>
                    <Nav.Link href="/products" className="nav-item" onClick={handleClose}>Products</Nav.Link>
                    <Nav.Link href="/about" className="nav-item" onClick={handleClose}>About Us</Nav.Link>
                    <Nav.Link href="/contact" className="nav-item" onClick={handleClose}>Contact</Nav.Link>
                </Nav>
            </Navbar.Collapse>
        </Navbar>
    );
}
