import { useState } from 'react';
import { Navbar, Nav, Offcanvas } from 'react-bootstrap';
import './NavBar.css';

export default function NavBar({ logo }) {
    const [show, setShow] = useState(false);

    const handleToggle = () => setShow(!show);
    const handleClose = () => setShow(false);

    return (
        <>
            <Navbar bg="light" expand="lg" className="menu-bar">
                <Navbar.Brand href="#">
                    <img
                        src={logo}
                        alt="Brand Logo"
                        className="brand-logo d-block d-lg-none mx-auto"
                    />
                </Navbar.Brand>
                <Navbar.Toggle onClick={handleToggle} aria-controls="offcanvasNavbar" />
            </Navbar>

            {/* Offcanvas Component for Mobile Navigation */}
            <Offcanvas show={show} onHide={handleClose} placement="end">
                <Offcanvas.Header closeButton>
                    <Offcanvas.Title>Menu</Offcanvas.Title>
                </Offcanvas.Header>
                <Offcanvas.Body>
                    <Nav className="flex-column">
                        <Nav.Link href="/" className="nav-item" onClick={handleClose}>Home</Nav.Link>
                        <Nav.Link href="/products" className="nav-item" onClick={handleClose}>Products</Nav.Link>
                        <Nav.Link href="/about" className="nav-item" onClick={handleClose}>About Us</Nav.Link>
                        <Nav.Link href="/contact" className="nav-item" onClick={handleClose}>Contact</Nav.Link>
                    </Nav>
                </Offcanvas.Body>
            </Offcanvas>
        </>
    );
}
