import React from 'react';
import './HomePage.css'; // Import CSS for styling
import ProductCard from '../components/ProductCard';
import Img1 from '../assets/Img1.webp';
import Img2 from '../assets/Img2.webp';
import Img3 from '../assets/Img3.webp';
import Img4 from '../assets/Img4.webp';
import gif from '../assets/gif.gif';
const HomePage = () => {


  const [scrolled, setScrolled] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      const scrollThreshold = 100; // Set your scroll threshold here
      if (window.scrollY > scrollThreshold) {
        setScrolled(true);  // Change background after scrolling down
      } else {
        setScrolled(false); // Reset background when at top
      }
    };

    window.addEventListener('scroll', handleScroll);

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);


  return (
   <>
    <div className="homepage-container">

      <img src={gif} className="img-container"/>
      <br/>
      <h2 className="featured-heading">Featured Products</h2> 
      <div className="product-card-container">
        <ProductCard image={Img1} title="PRODUCT 1" price="100" />
        <ProductCard image={Img2} title="PRODUCT 2" price="150" />
        <ProductCard image={Img3} title="PRODUCT 3" price="200" />
        <ProductCard image={Img4} title="PRODUCT 4" price="200" />
      </div>
    </div>
    </> 
  );
};

export default HomePage;
