import React, { useState } from "react";
import { Navbar, Nav, Offcanvas } from "react-bootstrap";
import { Link } from "react-router-dom"; // Import Link from react-router-dom
import logo from '../../../../assets/logo.jpg'; 
import './NavBar.css';

export default function NavBar({ logo }) {
    const [show, setShow] = useState(false);
    const handleToggle = () => setShow(!show);
    const handleClose = () => setShow(false);

    return (
        <>
            <Navbar bg="light" expand="lg" className="menu-bar">
                <Navbar.Toggle onClick={handleToggle} aria-controls="offcanvasNavbar" className="d-lg-none" />
                <Navbar.Brand href="#" className="d-lg-none">
                    <img src={logo} alt="Brand Logo" className="brand-logo mx-auto" />
                </Navbar.Brand>
                <Navbar.Collapse id="offcanvasNavbar" className="d-none d-lg-block">
                    <Nav className="me-auto">
                        <Link to="/home" className="nav-item"><Nav.Link>Home</Nav.Link></Link>
                        <Link to="/products" className="nav-item"><Nav.Link>Products</Nav.Link></Link>
                        <Link to="/about" className="nav-item"><Nav.Link>About Us</Nav.Link></Link>
                        <Link to="/contact" className="nav-item"><Nav.Link>Contact</Nav.Link></Link>
                    </Nav>
                </Navbar.Collapse>
            </Navbar>

            {/* Offcanvas Component for Mobile Navigation */}
            <Offcanvas show={show} onHide={handleClose} placement="start" className="d-lg-none">
                <Offcanvas.Header closeButton>
                    <Offcanvas.Title>Menu</Offcanvas.Title>
                </Offcanvas.Header>
                <Offcanvas.Body>
                    <Nav className="flex-column">
                        <Link to="/home" className="nav-item" onClick={handleClose}><Nav.Link>Home</Nav.Link></Link>
                        <Link to="/products" className="nav-item" onClick={handleClose}><Nav.Link>Products</Nav.Link></Link>
                        <Link to="/about" className="nav-item" onClick={handleClose}><Nav.Link>About Us</Nav.Link></Link>
                        <Link to="/contact" className="nav-item" onClick={handleClose}><Nav.Link>Contact</Nav.Link></Link>
                    </Nav>
                </Offcanvas.Body>
            </Offcanvas>
        </>
    );
}
