import React, { useState } from "react";
import "./FilterBar.css"; // Import styles for the filter bar
import { FaFilter } from "react-icons/fa";

const FilterBar = () => {
  const [selectedGender, setSelectedGender] = useState([]);
  const [selectedSize, setSelectedSize] = useState([]);
  const [showGenderOptions, setShowGenderOptions] = useState(false);
  const [showSizeOptions, setShowSizeOptions] = useState(false);
  const [genderFilterLabel, setGenderFilterLabel] = useState("Gender  +");
  const [sizeFilterLabel, setSizeFilterLabel] = useState("Size  +");
  const [isOffcanvasOpen, setIsOffcanvasOpen] = useState(false); // For offcanvas toggle

  const handleToggleGenderOptions = () => {
    setShowGenderOptions((prev) => !prev);
    setGenderFilterLabel((prev) => (showGenderOptions ? "Gender  +" : "Gender  -"));
  };

  const handleToggleSizeOptions = () => {
    setShowSizeOptions((prev) => !prev);
    setSizeFilterLabel((prev) => (showSizeOptions ? "Size  +" : "Size  -"));
  };

  const handleGenderSelect = (gender) => {
    setSelectedGender((prevSelected) =>
      prevSelected.includes(gender)
        ? prevSelected.filter((item) => item !== gender) // Deselect
        : [...prevSelected, gender] // Select
    );
  };

  const handleSizeSelect = (size) => {
    setSelectedSize((prevSelected) =>
      prevSelected.includes(size)
        ? prevSelected.filter((item) => item !== size) // Deselect
        : [...prevSelected, size] // Select
    );
  };

  // Function to toggle offcanvas filter on mobile
  const handleToggleOffcanvas = () => {
    setIsOffcanvasOpen((prev) => !prev);
  };

  return (
    <>
      {/* Filter Toggle Button */}
      <div className="filter-toggle" onClick={handleToggleOffcanvas}>
        <div className="filter-icon">
          <FaFilter size={24} />
          <span>Filter</span>
        </div>
      </div>

      {/* Offcanvas for Mobile View */}
      {isOffcanvasOpen && (
        <div className={`offcanvas-filter ${isOffcanvasOpen ? "show" : ""}`}>
          <div className="offcanvas-header">
            <h3>Filter Options</h3>
            <button className="close-offcanvas" onClick={handleToggleOffcanvas}>
              Ã—
            </button>
          </div>
          <div className="offcanvas-body">
            <div className="filter-group">
              <div className="filter-label" onClick={handleToggleGenderOptions}>
                {genderFilterLabel}
              </div>
              {showGenderOptions && (
                <div className="filter-options">
                  {["Male", "Female", "Boy", "Girl"].map((gender) => (
                    <div
                      key={gender}
                      className={`filter-option ${selectedGender.includes(gender) ? "selected" : ""}`}
                      onClick={() => handleGenderSelect(gender)}
                    >
                      {gender}
                    </div>
                  ))}
                </div>
              )}
            </div>

            <div className="filter-group">
              <div className="filter-label" onClick={handleToggleSizeOptions}>
                {sizeFilterLabel}
              </div>
              {showSizeOptions && (
                <div className="filter-options">
                  {["Small", "Medium", "Large", "Extra Large"].map((size) => (
                    <div
                      key={size}
                      className={`filter-option ${selectedSize.includes(size) ? "selected" : ""}`}
                      onClick={() => handleSizeSelect(size)}
                    >
                      {size}
                    </div>
                  ))}
                </div>
              )}
            </div>
          </div>
        </div>
      )}
    </>
  );
};

export default FilterBar;
