import React from 'react';
import { Card, Button } from 'react-bootstrap';
import './ProductCard.css'; // Create a separate CSS file for styles

export default function ProductCard({ image, title, price, onClick }) {
  return (
    <Card className="product-card" style={{ width: '18rem', margin: '10px', cursor: 'pointer' }} onClick={onClick}>
      <Card.Img 
        variant="top" 
        src={image} 
        alt={title} 
        style={{ height: '200px', objectFit: 'cover' }} 
        aria-label={`Image of ${title}`}
      />
      <Card.Body>
        <Card.Title>{title}</Card.Title>
        <Card.Text>
          <strong>Price:</strong> ${price}
        </Card.Text>
        <Button variant="primary">View Details</Button>
      </Card.Body>
    </Card>
  );
}
