import React, { useState } from "react";
import './Products.css'; // Add styles for Products page
import FilterMenu from '../components/FilterMenu'; // Assuming you create this component
import ProductCard from '../components/ProductCard';

// Sample product data
const products = [
  { id: 1, title: "Product 1", price: "100", image: "Img1.webp" },
  { id: 2, title: "Product 2", price: "150", image: "Img2.webp" },
  { id: 3, title: "Product 3", price: "200", image: "Img3.webp" },
  { id: 4, title: "Product 4", price: "200", image: "Img4.webp" },
  // Add more products as needed
];

const Products = () => {
  const [filteredProducts, setFilteredProducts] = useState(products);

  // Function to handle filtering logic (you can modify this as needed)
  const handleFilter = (filterCriteria) => {
    const newFilteredProducts = products.filter(product => {
      // Example filter logic
      return product.price <= filterCriteria.price; // Example filter
    });
    setFilteredProducts(newFilteredProducts);
  };

  return (
    <div className="products-container">
      <div className="filter-menu">
        <FilterMenu onFilter={handleFilter} /> {/* Pass the filter function */}
      </div>
      <div className="product-card-container">
        {filteredProducts.map(product => (
          <ProductCard
            key={product.id}
            image={require(`../assets/${product.image}`).default} // Adjust the path as needed
            title={product.title}
            price={product.price}
          />
        ))}
      </div>
    </div>
  );
};

export default Products;
