import { useState } from 'react';
import { Navbar, Nav, Offcanvas } from 'react-bootstrap';
import { NavLink } from 'react-router-dom';
import './NavBar.css';

export default function NavBar({ logo }) {
    const [show, setShow] = useState(false);

    const handleToggle = () => setShow(true);
    const handleClose = () => setShow(false);

    return (
        <>
            <Navbar bg="light" expand="lg" className="menu-bar">
                <Navbar.Toggle onClick={handleToggle} aria-controls="offcanvasNavbar" className="d-lg-none" />
                <Navbar.Brand href="#" className="d-lg-none">
                    <img
                        src={logo}
                        alt="Brand Logo"
                        className="brand-logo mx-auto"
                    />
                </Navbar.Brand>
                <Navbar.Collapse id="offcanvasNavbar" className="d-none d-lg-block">
                    <Nav className="me-auto">
                        <NavLink to="/home" className="nav-item">Home</NavLink>
                        <NavLink to="/products" className="nav-item">Products</NavLink>
                        <NavLink to="/about" className="nav-item">About Us</NavLink>
                        <NavLink to="/contact" className="nav-item">Contact</NavLink>
                    </Nav>
                </Navbar.Collapse>
            </Navbar>

            {/* Offcanvas Component for Mobile Navigation */}
            <Offcanvas show={show} onHide={handleClose} placement="start" className="d-lg-none">
                <Offcanvas.Header closeButton>
                    <Offcanvas.Title>Menu</Offcanvas.Title>
                </Offcanvas.Header>
                <Offcanvas.Body>
                    <Nav className="flex-column">
                        <Nav.Link href="/" className="nav-item" onClick={handleClose}>Home</Nav.Link>
                        <Nav.Link href="/products" className="nav-item" onClick={handleClose}>Products</Nav.Link>
                        <Nav.Link href="/about" className="nav-item" onClick={handleClose}>About Us</Nav.Link>
                        <Nav.Link href="/contact" className="nav-item" onClick={handleClose}>Contact</Nav.Link>
                    </Nav>
                </Offcanvas.Body>
            </Offcanvas>
        </>
    );
}
