import React, { useState } from 'react';
import { Container, Row } from 'react-bootstrap';
import Header from './Header/Header';
import LeftNavbar from './LeftNavbar/LeftNavbar';
import ContentArea from './ContentArea/ContentArea';
import 'bootstrap/dist/css/bootstrap.min.css'; // Ensure you import Bootstrap CSS

function Layout({ children }) {
    const [expanded, setExpanded] = useState(true); // State to control the sidebar visibility

    const toggleSidebar = () => {
        setExpanded(!expanded); // Toggle the expanded state
    };

    return (
        <Container fluid>
            <Row >
                <Header expanded={expanded} toggleSidebar={toggleSidebar} />
            </Row>
            <Row>
                <LeftNavbar expanded={expanded} />
                <ContentArea expanded={expanded}/>
            </Row>
        </Container>
    );
}

export default Layout;
